mapper.select:

     and ORGAN_NAME like #{organName} 
     and ORGAN_NO like #{organNo} 
     and ORGAN_CODE like #{organCode} 
     and ORGAN_PHONE like #{organPhone} 
     and ORGAN_POST like #{organPost} 
     and ORGAN_TYPE_VALUE like #{organTypeValue} 
     and ORGAN_ADDRESS like #{organAddress} 
     and ORGAN_CONTACTOR like #{organContactor} 
     and ORGAN_CONTACTOR_MOBILE like #{organContactorMobile} 
     and ORGAN_DOMAIN like #{organDomain} 
     and ORGAN_FAX like #{organFax} 
     and ORGAN_IS_VIRTUAL like #{organIsVirtual} 
     and ORGAN_PARENT_ID like #{organParentId} 
     and ORGAN_PARENT_ID_LIST like #{organParentIdList} 
     and ORGAN_SHORT_NAME like #{organShortName} 
     and ORGAN_STATUS like #{organStatus} 
     and ORGAN_CREATRE_ORG_ID like #{organCreatreOrgId} 
     and ORGAN_OUTER_ID like #{organOuterId} 
     and ORGAN_OUTER_TYPE like #{organOuterType} 
ORDER by ORGAN_ORDER_NUM DESC
