mapper.select:
selectfrom TB4D_DATASET_COLUMN
         and COLUMN_DS_ID like #{columnDsId} 
         and COLUMN_CAPTION like #{columnCaption} 
         and COLUMN_NAME like #{columnName} 
         and COLUMN_CREATER like #{columnCreater} 
         and COLUMN_UPDATER like #{columnUpdater} 
         and COLUMN_DESC like #{columnDesc} 
         and COLUMN_DEFAULT_TYPE like #{columnDefaultType} 
         and COLUMN_DEFAULT_VALUE like #{columnDefaultValue} 
         and COLUMN_DEFAULT_TEXT like #{columnDefaultText} 
         and COLUMN_TABLE_NAME like #{columnTableName} 
    ORDER by COLUMN_ORDER_NUM DESC
