mapper.select:
selectfrom TB4D_DATASET
         and DS_CAPTION like #{dsCaption} 
         and DS_NAME like #{dsName} 
         and DS_ORGAN_ID like #{dsOrganId} 
         and DS_CREATER like #{dsCreater} 
         and DS_UPDATER like #{dsUpdater} 
         and DS_TYPE like #{dsType} 
         and DS_ISSYSTEM like #{dsIssystem} 
         and DS_DESC like #{dsDesc} 
         and DS_GROUP_ID like #{dsGroupId} 
         and DS_STATUS like #{dsStatus} 
         and DS_SQL_SELECT_TEXT like #{dsSqlSelectText} 
         and DS_SQL_SELECT_VALUE like #{dsSqlSelectValue} 
         and DS_CLASS_NAME like #{dsClassName} 
         and DS_REST_STRUCTURE_URL like #{dsRestStructureUrl} 
         and DS_REST_DATA_URL like #{dsRestDataUrl} 
    ORDER by DS_ORDER_NUM DESC

selectfrom TB4D_DATASET
        where DS_GROUP_ID=#{groupId}
        ORDER by DS_ORDER_NUM DESC
