mapper.select:
selectfrom TB4D_DATASET_GROUP
         and DS_GROUP_VALUE like #{dsGroupValue} 
         and DS_GROUP_TEXT like #{dsGroupText} 
         and DS_GROUP_DESC like #{dsGroupDesc} 
         and DS_GROUP_STATUS like #{dsGroupStatus} 
         and DS_GROUP_PARENT_ID like #{dsGroupParentId} 
         and DS_GROUP_ISSYSTEM like #{dsGroupIssystem} 
         and DS_GROUP_DEL_ENABLE like #{dsGroupDelEnable} 
         and DS_GROUP_PID_LIST like #{dsGroupPidList} 
    ORDER by DS_GROUP_ORDER_NUM DESC
