mapper.select:
selectfrom TBUILD_DATASET_RELATED_TABLE
         and RT_DS_ID like #{rtDsId} 
         and RT_TABLE_NAME like #{rtTableName} 
         and RT_TABLE_CAPTION like #{rtTableCaption} 
         and RT_TABLE_ID like #{rtTableId} 
         and RT_TABLE_TYPE like #{rtTableType} 
         and RT_DESC like #{rtDesc} 
    ORDER by RT_ORDER_NUM DESC
