mapper.select:
selectfrom TBUILD_DB_LINK
         and DB_VALUE like #{dbValue} 
         and DB_NAME like #{dbName} 
         and DB_TYPE like #{dbType} 
         and DB_DRIVER_NAME like #{dbDriverName} 
         and DB_DATABASE_NAME like #{dbDatabaseName} 
         and DB_URL like #{dbUrl} 
         and DB_USER like #{dbUser} 
         and DB_PASSWORD like #{dbPassword} 
         and DB_DESC like #{dbDesc} 
         and DB_IS_LOCATION like #{dbIsLocation} 
         and DB_STATUS like #{dbStatus} 
         and DB_ORGAN_ID like #{dbOrganId} 
         and DB_ORGAN_NAME like #{dbOrganName} 
    ORDER by DB_ORDER_NUM DESC
