mapper.select:
selectfrom TB4D_FLOW_MODEL
         and MODEL_DE_ID like #{modelDeId} 
         and MODEL_MODULE_ID like #{modelModuleId} 
         and MODEL_GROUP_ID like #{modelGroupId} 
         and MODEL_NAME like #{modelName} 
         and MODEL_CREATER like #{modelCreater} 
         and MODEL_UPDATER like #{modelUpdater} 
         and MODEL_DESC like #{modelDesc} 
         and MODEL_STATUS like #{modelStatus} 
         and MODEL_DEPLOYMENT_ID like #{modelDeploymentId} 
         and MODEL_START_KEY like #{modelStartKey} 
         and MODEL_RESOURCE_NAME like #{modelResourceName} 
         and MODEL_FROM_TYPE like #{modelFromType} 
    ORDER by MODEL_ORDER_NUM DESC
