mapper.select:
selectfrom TBUILD_FORM_RESOURCE
         and FORM_CODE like #{formCode} 
         and FORM_NAME like #{formName} 
         and FORM_SINGLE_NAME like #{formSingleName} 
         and FORM_CREATER like #{formCreater} 
         and FORM_UPDATER like #{formUpdater} 
         and FORM_TYPE like #{formType} 
         and FORM_ISSYSTEM like #{formIssystem} 
         and FORM_DESC like #{formDesc} 
         and FORM_MODULE_ID like #{formModuleId} 
         and FORM_STATUS like #{formStatus} 
         and FORM_ORGAN_ID like #{formOrganId} 
         and FORM_ORGAN_NAME like #{formOrganName} 
         and FORM_MAIN_TABLE_NAME like #{formMainTableName} 
         and FORM_MAIN_TABLE_CAPTION like #{formMainTableCaption} 
         and FORM_DATA_RELATION like #{formDataRelation} 
         and FORM_IS_TEMPLATE like #{formIsTemplate} 
         and FORM_IS_RESOLVE like #{formIsResolve} 
         and FORM_EVERY_TIME_RESOLVE like #{formEveryTimeResolve} 
         and FORM_SOURCE like #{formSource} 
         and FORM_CONTENT_URL like #{formContentUrl} 
         and FORM_THEME like #{formTheme} 
         and FORM_CUST_SERVER_RENDERER like #{formCustServerRenderer} 
         and FORM_CUST_REF_JS like #{formCustRefJs} 
         and FORM_CUST_CLIENT_RENDERER like #{formCustClientRenderer} 
         and FORM_HTML_SOURCE like #{formHtmlSource} 
         and FORM_HTML_RESOLVE like #{formHtmlResolve} 
         and FORM_JS_CONTENT like #{formJsContent} 
         and FORM_CSS_CONTENT like #{formCssContent} 
         and FORM_CONFIG_CONTENT like #{formConfigContent} 
    ORDER by FORM_ORDER_NUM DESC
