mapper.select:
selectfrom TBUILD_LIST_RESOURCE
         and LIST_CODE like #{listCode} 
         and LIST_NAME like #{listName} 
         and LIST_SINGLE_NAME like #{listSingleName} 
         and LIST_CREATER like #{listCreater} 
         and LIST_UPDATER like #{listUpdater} 
         and LIST_TYPE like #{listType} 
         and LIST_ISSYSTEM like #{listIssystem} 
         and LIST_DESC like #{listDesc} 
         and LIST_MODULE_ID like #{listModuleId} 
         and LIST_STATUS like #{listStatus} 
         and LIST_ORGAN_ID like #{listOrganId} 
         and LIST_ORGAN_NAME like #{listOrganName} 
         and LIST_DATASET_ID like #{listDatasetId} 
         and LIST_ENABLE_S_SEAR like #{listEnableSSear} 
         and LIST_ENABLE_C_SEAR like #{listEnableCSear} 
         and LIST_HTML_SOURCE like #{listHtmlSource} 
         and LIST_HTML_RESOLVE like #{listHtmlResolve} 
         and LIST_JS_CONTENT like #{listJsContent} 
         and LIST_CONFIG_CONTENT like #{listConfigContent} 
    ORDER by LIST_ORDER_NUM DESC
