mapper.select:
selectfrom TB4D_MODULE
         and MODULE_VALUE like #{moduleValue} 
         and MODULE_TEXT like #{moduleText} 
         and MODULE_DESC like #{moduleDesc} 
         and MODULE_STATUS like #{moduleStatus} 
         and MODULE_PARENT_ID like #{moduleParentId} 
         and MODULE_ISSYSTEM like #{moduleIssystem} 
         and MODULE_DEL_ENABLE like #{moduleDelEnable} 
         and MODULE_PID_LIST like #{modulePidList} 
         and MODULE_ORGAN_ID like #{moduleOrganId} 
         and MODULE_ORGAN_NAME like #{moduleOrganName} 
    ORDER by MODULE_ORDER_NUM DESC
