mapper.select:
selectfrom TB4D_SERVICE_LINK
         and LINK_VALUE like #{linkValue} 
         and LINK_NAME like #{linkName} 
         and LINK_TYPE like #{linkType} 
         and LINK_URL like #{linkUrl} 
         and LINK_PARAS like #{linkParas} 
         and LINK_USER like #{linkUser} 
         and LINK_PASSWORD like #{linkPassword} 
         and LINK_DESC like #{linkDesc} 
         and LINK_IS_LOCATION like #{linkIsLocation} 
         and LINK_STATUS like #{linkStatus} 
         and LINK_ORGAN_ID like #{linkOrganId} 
         and LINK_ORGAN_NAME like #{linkOrganName} 
    ORDER by LINK_ORDER_NUM DESC
