mapper.select:
selectfrom TB4D_TABLE
         and TABLE_CAPTION like #{tableCaption} 
         and TABLE_NAME like #{tableName} 
         and TABLE_DBNAME like #{tableDbname} 
         and TABLE_CREATER like #{tableCreater} 
         and TABLE_UPDATER like #{tableUpdater} 
         and TABLE_SERVICE_VALUE like #{tableServiceValue} 
         and TABLE_TYPE like #{tableType} 
         and TABLE_ISSYSTEM like #{tableIssystem} 
         and TABLE_DESC like #{tableDesc} 
         and TABLE_GROUP_ID like #{tableGroupId} 
    ORDER by TABLE_ORDER_NUM DESC
