mapper.select:
selectfrom TBUILD_TABLE_FIELD
         and FIELD_TABLE_ID like #{fieldTableId} 
         and FIELD_NAME like #{fieldName} 
         and FIELD_CAPTION like #{fieldCaption} 
         and FIELD_ALLOW_NULL like #{fieldAllowNull} 
         and FIELD_DATA_TYPE like #{fieldDataType} 
         and FIELD_DEFAULT_VALUE like #{fieldDefaultValue} 
         and FIELD_CREATER like #{fieldCreater} 
         and FIELD_UPDATER like #{fieldUpdater} 
         and FIELD_DESC like #{fieldDesc} 
    ORDER by FIELD_ORDER_NUM DESC
