mapper.select:
selectfrom TB4D_TABLE_GROUP

            and TABLE_GROUP_VALUE like #{tableGroupValue}
            and TABLE_GROUP_TEXT like #{tableGroupText}
            and TABLE_GROUP_DESC like #{tableGroupDesc}
            and TABLE_GROUP_STATUS like #{tableGroupStatus}
            and TABLE_GROUP_PARENT_ID like #{tableGroupParentId}
            and TABLE_GROUP_ISSYSTEM like #{tableGroupIssystem}
            and TABLE_GROUP_DEL_ENABLE like #{tableGroupDelEnable}
            and TABLE_GROUP_ENP_ITEM like #{tableGroupEnpItem}

        ORDER by TABLE_GROUP_ORDER_NUM ASC
