mapper.select:
selectfrom TB4D_TABLE_RELATION
         and RELATION_GROUP_ID like #{relationGroupId} 
         and RELATION_NAME like #{relationName} 
         and RELATION_USER_ID like #{relationUserId} 
         and RELATION_USER_NAME like #{relationUserName} 
         and RELATION_DESC like #{relationDesc} 
         and RELATION_STATUS like #{relationStatus} 
         and RELATION_CONTENT like #{relationContent} 
    ORDER by RELATION_ORDER_NUM DESC
