mapper.select:
selectfrom TB4D_TABLE_RELATION_HIS
         and HIS_GROUP_ID like #{hisGroupId} 
         and HIS_NAME like #{hisName} 
         and HIS_USER_ID like #{hisUserId} 
         and HIS_USER_NAME like #{hisUserName} 
         and HIS_DESC like #{hisDesc} 
         and HIS_STATUS like #{hisStatus} 
         and HIS_BELONG_REL_ID like #{hisBelongRelId} 
         and HIS_CONTENT like #{hisContent} 
    ORDER by HIS_ORDER_NUM DESC
