mapper.select:
selectfrom TSSO_DEPARTMENT
         and DEPT_NAME like #{deptName} 
         and DEPT_SHORT_NAME like #{deptShortName} 
         and DEPT_NO like #{deptNo} 
         and DEPT_PER_CHARGE like #{deptPerCharge} 
         and DEPT_PER_CHARGE_PHONE like #{deptPerChargePhone} 
         and DEPT_IS_VIRTUAL like #{deptIsVirtual} 
         and DEPT_CREATE_USER_ID like #{deptCreateUserId} 
         and DEPT_IS_ROOT like #{deptIsRoot} 
         and DEPT_PARENT_ID like #{deptParentId} 
         and DEPT_PARENT_ID_LIST like #{deptParentIdList} 
         and DEPT_STATUS like #{deptStatus} 
         and DEPT_ORGAN_ID like #{deptOrganId} 
         and DEPT_DESC like #{deptDesc} 
    ORDER by DEPT_ORDER_NUM DESC
