mapper.select:
selectfrom TB4D_DEPARTMENT_USER
         and DU_DEPT_ID like #{duDeptId} 
         and DU_USER_ID like #{duUserId} 
         and DU_IS_MAIN like #{duIsMain} 
         and DU_TITLE like #{duTitle} 
         and DU_DESC like #{duDesc} 
         and DU_CREATE_USER_ID like #{duCreateUserId} 
         and DU_STATUS like #{duStatus} 
    ORDER by DU_ORDER_NUM DESC

SELECT * FROM TB4D_DEPARTMENT_USER JOIN TB4D_USER ON TB4D_DEPARTMENT_USER.DU_USER_ID=TB4D_USER.USER_ID JOIN TB4D_DEPARTMENT ON TB4D_DEPARTMENT_USER.DU_DEPT_ID=TB4D_DEPARTMENT.DEPT_ID JOIN TB4D_ORGAN ON TB4D_DEPARTMENT.DEPT_ORGAN_ID=TB4D_ORGAN.ORGAN_ID

     and TB4D_USER.USER_NAME like #{userName} 
     and TB4D_USER.USER_ACCOUNT like #{account} 
     and TB4D_USER.USER_PHONE_NUMBER like #{userPhoneNumber} 
     and TB4D_DEPARTMENT_USER.DU_DEPT_ID like #{departmentId} 

ORDER by DU_ORDER_NUM asc
