mapper.select:
selectfrom TB4D_DEPARTMENT_USER
         and DU_DEPT_ID like #{duDeptId} 
         and DU_USER_ID like #{duUserId} 
         and DU_IS_MAIN like #{duIsMain} 
         and DU_TITLE like #{duTitle} 
         and DU_DESC like #{duDesc} 
         and DU_CREATE_USER_ID like #{duCreateUserId} 
         and DU_STATUS like #{duStatus} 
    ORDER by DU_ORDER_NUM DESC
