mapper.select:
selectfrom TSSO_ROLE
         and ROLE_KEY like #{roleKey} 
         and ROLE_NAME like #{roleName} 
         and ROLE_GROUP_ID like #{roleGroupId} 
         and ROLE_DESC like #{roleDesc} 
         and ROLE_STATUS like #{roleStatus} 
         and ROLE_CREATER_ID like #{roleCreaterId} 
         and ROLE_ORGAN_ID like #{roleOrganId} 
    ORDER by ROLE_ORDER_NUM DESC
