mapper.select:
selectfrom TB4D_SSO_APP
         and APP_CODE like #{appCode} 
         and APP_NAME like #{appName} 
         and APP_PUBLIC_KEY like #{appPublicKey} 
         and APP_PRIVATE_KEY like #{appPrivateKey} 
         and APP_DOMAIN like #{appDomain} 
         and APP_INDEX_URL like #{appIndexUrl} 
         and APP_INTEGRATED_TYPE like #{appIntegratedType} 
         and APP_MAIN_IMAGE_ID like #{appMainImageId} 
         and APP_TYPE like #{appType} 
         and APP_TYPE_MAIN_ID like #{appTypeMainId} 
         and APP_DESC like #{appDesc} 
         and APP_STATUS like #{appStatus} 
         and APP_CREATER_ID like #{appCreaterId} 
         and APP_ORGAN_ID like #{appOrganId} 
    ORDER by APP_ORDER_NUM DESC
