mapper.select:
selectfrom TSSO_SSO_APP_FILE
         and APP_BELONG_APP_ID like #{appBelongAppId} 
         and APP_FILE_NAME like #{appFileName} 
         and APP_FILE_IS_MAIN like #{appFileIsMain} 
         and APP_FILE_DESC like #{appFileDesc} 
         and APP_FILE_STATUS like #{appFileStatus} 
         and APP_FILE_CREATER_ID like #{appFileCreaterId} 
         and APP_FILE_ORGAN_ID like #{appFileOrganId} 
         and APP_FILE_CNT_FILE_ID like #{appFileCntFileId} 
    ORDER by APP_FILE_ORDER_NUM DESC
