mapper.select:
selectfrom TB4D_SSO_APP_INTERFACE
         and INTERFACE_BELONG_APP_ID like #{interfaceBelongAppId} 
         and INTERFACE_CODE like #{interfaceCode} 
         and INTERFACE_NAME like #{interfaceName} 
         and INTERFACE_URL like #{interfaceUrl} 
         and INTERFACE_PARAS like #{interfaceParas} 
         and INTERFACE_FORMAT like #{interfaceFormat} 
         and INTERFACE_DESC like #{interfaceDesc} 
         and INTERFACE_STATUS like #{interfaceStatus} 
         and INTERFACE_CREATER_ID like #{interfaceCreaterId} 
         and INTERFACE_ORGAN_ID like #{interfaceOrganId} 
    ORDER by INTERFACE_ORDER_NUM DESC
