mapper.select:
selectfrom TB4D_SSO_APP_INTERFACE
         and INTERFACE_BELONG_APP_ID like #{interfaceBelongAppId} 
         and INTERFACE_CODE like #{interfaceCode} 
         and INTERFACE_NAME like #{interfaceName} 
         and INTERFACE_URL like #{interfaceUrl} 
         and INTERFACE_URL_PARAS like #{interfaceUrlParas} 
         and INTERFACE_URL_FORMAT like #{interfaceUrlFormat} 
         and INTERFACE_URL_DESC like #{interfaceUrlDesc} 
         and INTERFACE_URL_STATUS like #{interfaceUrlStatus} 
         and INTERFACE_URL_CREATER_ID like #{interfaceUrlCreaterId} 
         and INTERFACE_URL_ORGAN_ID like #{interfaceUrlOrganId} 
    ORDER by INTERFACE_URL_ORDER_NUM DESC
