mapper.select:
selectfrom TB4D_USER
         and USER_NAME like #{userName} 
         and USER_ACCOUNT like #{userAccount} 
         and USER_PASSWORD like #{userPassword} 
         and USER_EMAIL like #{userEmail} 
         and USER_PHONE_NUMBER like #{userPhoneNumber} 
         and USER_HEAD_ID like #{userHeadId} 
         and USER_ORGAN_ID like #{userOrganId} 
         and USER_DESC like #{userDesc} 
         and USER_CREATE_USER_ID like #{userCreateUserId} 
         and USER_STATUS like #{userStatus} 
    ORDER by USER_ORDER_NUM DESC
