mapper.select:
selectfrom TB4D_HISTORY_DATA
         and HISTORY_ORGAN_ID like #{historyOrganId} 
         and HISTORY_ORGAN_NAME like #{historyOrganName} 
         and HISTORY_USER_ID like #{historyUserId} 
         and HISTORY_USER_NAME like #{historyUserName} 
         and HISTORY_TABLE_NAME like #{historyTableName} 
         and HISTORY_RECORD_ID like #{historyRecordId} 
         and HISTORY_RECORD like #{historyRecord} 
    ORDER by FORM_ORDER_NUM DESC
